/**
 * Hardware Wallet Verified Vectors
 *
 * These vectors are generated by actual hardware wallets and serve as the
 * ultimate ground truth for address derivation. If our implementation
 * produces different addresses than hardware wallets, FUNDS WILL BE LOST.
 *
 * To add new vectors:
 * 1. Use the seed mnemonic on a hardware wallet
 * 2. Export addresses or verify them on-device
 * 3. Record the device model, firmware version, and date verified
 * 4. Add to the appropriate section below
 *
 * DO NOT MODIFY EXISTING VECTORS without re-verification on hardware.
 */

export interface HardwareWalletVector {
  // Verification provenance
  device: string; // e.g., "Coldcard Mk4", "Ledger Nano S Plus", "Trezor Model T"
  firmware: string; // e.g., "5.2.0"
  verifiedDate: string; // ISO date: "2024-01-15"
  verifiedBy?: string; // Optional: who verified this

  // Derivation parameters
  mnemonic: string; // The seed used (should be test mnemonics only!)
  passphrase?: string; // BIP-39 passphrase if used
  network: 'mainnet' | 'testnet';
  scriptType: 'legacy' | 'nested_segwit' | 'native_segwit' | 'taproot';
  accountIndex: number;

  // Expected xpub at account level
  expectedXpub: string;
  derivationPath: string; // Full path to xpub, e.g., "m/84'/0'/0'"

  // Expected addresses
  addresses: {
    index: number;
    receive: string;
    change: string;
  }[];
}

export interface MultisigHardwareWalletVector {
  // Verification provenance
  devices: {
    device: string;
    firmware: string;
    verifiedDate: string;
  }[];

  // Multisig configuration
  threshold: number;
  totalSigners: number;
  network: 'mainnet' | 'testnet';
  scriptType: 'p2sh' | 'p2sh_p2wsh' | 'p2wsh';

  // Per-device xpubs (in order of device verification)
  signers: {
    mnemonic: string;
    derivationPath: string;
    xpub: string;
  }[];

  // Expected multisig addresses
  addresses: {
    index: number;
    receive: string;
    change: string;
  }[];
}

